package com.javadiscord.jdi.utils;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.Test;

import java.io.IOException;
import java.net.URI;
import java.nio.file.Path;
import java.nio.file.Paths;

final class DiscordImageUtilTest {

    private static final String IMAGE_JPG_BASE64 =
            "";

    private Path getPathFromString(String path) throws Exception {
        URI uri = DiscordImageUtilTest.class.getResource(path).toURI();

        return Paths.get(uri);
    }

    @Test
    void testBase64EncodePng() throws Exception {
        Path path = getPathFromString("/image.jpg");

        assertEquals(IMAGE_JPG_BASE64, DiscordImageUtil.base64Encode(path));
    }

    @Test
    void testNullImage() {
        assertThrows(
                NullPointerException.class,
                () -> {
                    DiscordImageUtil.base64Encode(null);
                });
    }

    @Test
    void testNotImage() throws Exception {
        Path path = getPathFromString("/file.txt");

        assertThrows(IOException.class, () -> DiscordImageUtil.base64Encode(path));
    }

    @Test
    void testToDiscordString() throws Exception {
        Path path = getPathFromString("/image.jpg");

        String expected = String.format("data:image/jpg;base64,%s", IMAGE_JPG_BASE64);
        assertEquals(expected, DiscordImageUtil.toDiscordString(path));
    }
}
